var buttons=document.querySelectorAll(".btn-buy"),orderPopup=document.querySelector(".modal-order"),orderClose=orderPopup.querySelector(".modal-close");buttons.forEach(function(a){a.addEventListener("click",function(){event.preventDefault();orderPopup.classList.add("modal-show")})});orderClose.addEventListener("click",function(a){a.preventDefault();orderPopup.classList.remove("modal-show")});
window.addEventListener("keydown",function(a){27===a.keyCode&&orderPopup.classList.contains("modal-show")&&(a.preventDefault(),orderPopup.classList.remove("modal-show"))});
